body {
 margin: 20px;
 padding: 0;
 background-color: #FFF9F5;
 color: #36454F;
 font-family: Arial, sans-serif;
}

button {
 background-color: #00BFFF;
 border: none;
 color: white;
 padding: 15px 30px;
 text-align: center;
 text-decoration: none;
 display: inline-block;
 font-size: 16px;
 border-radius: 10px;
}

div {
 width: 400px;
 margin: auto;
}

var banner = document.getElementById("banner");
var message = document.getElementById("message");
var count = 0;
var changeCount = () => banner.innerHTML = "Car Count: " + count;

function errorMessage() {
  message.innerHTML = "No Cars To Subtract";
}

function changeMessage(x) {
  message.innerHTML = "One car was " + x;
}

function add() {
  count += 1;
  changeCount();
  changeMessage("added");
}

function subtract() {
  if (count > 0) {
    count -= 1;
    changeCount();
    changeMessage("subtracted");
  } else {
    errorMessage();
  }
}
const buttonAdd = document.getElementById("add");
const buttonSubtract = document.getElementById("subtract");

