body {
 background-color: azure;
 text-align: center;
 font-family: 'Merriweather Sans', sans-serif;
}

h2 {
 background-color: gold;
 padding: 10px;
}

input {
 padding: 10px;
 font-family: Quicksand;
}

button {
 padding: 10px;
 background-color: red;
 border: none;
 color: white;
}

