@import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300&display=swap');

body {
 background-color: whitesmoke;
 text-align: center;
 font-family: 'Poppins', sans-serif;
 display: grid;
}

form {
 width: 80%;
 margin: auto;
}

h2 {
 padding: 10px;
}

input {
 padding: 10px;
 margin: 10px;
 font-family: Quicksand;
}

label {
 margin: 10px;
}

button {
 padding: 10px;
 background-color: green;
 border: none;
 color: white;
}

@import url('https://fonts.googleapis.com/css2?family=Fredoka+One&family=Lato:wght@300&display=swap');

h1 {
 font-family: 'Fredoka One', cursive;
}

body {
 text-align: center;
 font-family: Lato;
 display: grid;
 background-color: white;
}

form {
 width: 80%;
 margin: auto;
}

label {
 float: left;
 width: 50%;
}

input {
 width: 50%;
 margin: 10px;
 padding: 10px;
 background: white;
 font-family: Lato;
}

button {
 background: teal;
 font-size: 15px;
 padding: 10px;
 border: none;
 color: white;
}
