let accountBalance = 6500;

function deposit(amount) {
  console.log("Depositing: $" + amount);
  accountBalance += amount;
  return true;
}

function withdraw(amount) {
  console.log("Withdrawing: $" + amount);
  accountBalance -= amount;
  return true;
}

function balance() {
  return "Your balance is currently: $" + accountBalance;
}

function greeting(name) {
  return "Welcome to your automated banking portal, " + name + ".";
}
console.log(greeting("Jane"));
console.log(balance());
if (deposit(50)) {
  console.log(balance());
}
if (withdraw(1200)) {
  console.log(balance());
}
if (withdraw(6600)) {
  console.log(balance());
}

