      <p>Select the plant type you enjoy eating more:</p>
      <select value={value} onChange={handleSelect}>
        <option value="fruits">Fruits</option>
        <option value="vegetables">Vegetables</option>
      </select>
      {value == "fruits" ? <Fruits /> : <Vegetables />}
    </div>
  );
};

export default App;

