files.content:
body {
  text-align: center;
  background-color: slateblue;
  font-family: 'Trebuchet MS';
  color: white;
}

h2 {
  font-size: 24px;
}

a {
  background-color: white;
  font-size: 20px;
  color: slateblue;
  padding: 10px;
  border-radius: 10px;
  margin: 10px;
  display: block;
  text-decoration: none;
}

