import React from "react";
import "./App.css";

const App = (props) => {
  return (
    <div className="price_tag">
      <div className="details">
        <h1>${props.member ? props.price * 0.75 : props.price} </h1>
        <p>{props.item}</p>
      </div>
      <div className="bar"></div>
    </div>
  );
};

export default App;

