html {
 font-family: Arial, sans-serif;
}
h1,h2,h3,h4,h5,h6 {
 margin-top:0px;
 margin-bottom:8px;
}
input, textarea, select, button {
 border:2px solid rgb(200,200,200);
 border-radius:16px;
 background-color:rgb(240,240,240);
 color:rgb(25,25,25);
 padding:8px;
 box-sizing: border-box;
}
button {
 cursor:pointer;
}
button:hover {
 background-color:rgb(230,230,230);
}
img {
 width:100%;
}
.price_tag {
 border:2px solid black;
 padding:8px;
 box-sizing:border-box;
}
.details {
 display:flex;
 flex-direction:row;
 justify-content: flex-start;
 align-items: center;
 flex-wrap: nowrap;
 margin-bottom:8px;
}
.details p, h1 {
 margin:0px;
}
.details h1 {
 margin-right:16px;
 flex-shrink:0;
}
.bar {
 width:75%;
 height:24px;
 background-color: black;
}

import React from "react";
import { createRoot } from "react-dom/client";
import App from "./App";

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);
root.render(<App />);

import React from "react";
import "./App.css";

const App = (props) => {
  if (props) {
    return <p>No Image</p>;
  }
  return <img src={props.src} />;
};

export default App;

