body {
  margin: auto;
  background-color: #FFF9F5;
  color: #36454F;
  font-family: Arial, Helvetica, sans-serif;
}

.heading {}

.navContainer {}

#navPhoto {
  text-align: right;
}

a {}

.headlineContainer {
  height: 400px;
}

.headlineImage {
  width: 100px;
  height: 100px;
  border-radius: 5%;
}

.headlineItem h3 {
  margin: 15px 5px 5px 5px;
}

.column {}

.colItem {
  width: 95%;
  margin: 8px;
  border: 2px solid #F0EEEE;
  border-radius: 2px;
}

.navItem {
  width: 220px;
}

