  height: 100px;
  width: 45%;
  margin: 10px;
  border: 2px solid #C0C0C0;
  border-radius: 4px;
  background-color: #C2C2C2;
}

.headlineItem:hover,
.colItem:hover {
  cursor: pointer;
}

.colItem h4 {
  margin: 5px;
}

.colItem p {
  margin: 3px;
}

