let cleanDishes = [false, false];
let allClean = false;

function loadDishwasher() {
 return new Promise(function(resolve) {
  for (let i = 0; i < cleanDishes.length; i++) {
   if (!cleanDishes[i]) {
    cleanDishes[i] = true;
   }
  }
  setTimeout(function() {
   allClean = true;
   console.log(
    `All ${cleanDishes.length} dishes are now clean...`);
   resolve(allClean);
  }, 1000);
 });
}

function putDishesAway(areAllDishesClean) {
 return new Promise(function(resolve, reject) {
  if (areAllDishesClean) {
   setTimeout(function() {
    resolve("No more dishes to wash.");
   }, 1000);
  } else {
   setTimeout(function() {
    reject("Some dishes are not clean yet.");
   }, 1000);
  }
 });
}

function startDishWashingProcess() {
 return loadDishwasher()
 .then(function(allDishesClean) {
  return putDishesAway(allDishesClean)
  .(function(result) {
   console.log(result);
  }).(function(error) {
   console.log(error);
  });
 });
}
startDishWashingProcess();

