function reverse(array) {
  const reversed = [];
  for (let i = array.length - 1; i > -1; i--) {
    reversed.push(array[i]);
  }
  return reversed;
}

function smallest(array) {
  let smallest = array[0];
  for (let i = 0; i < array.length; i++) {
    if (array[i] < smallest) {
      smallest = array[i];
    }
  }
  return smallest;
}

function differenceMinMax(array) {
  let biggest = array[0];
  let smallest = array[0];
  for (let i = 0; i < array.length; i++) {
    if (array[i] > biggest) {
      biggest = array[i];
    }
    if (array[i] < smallest) {
      smallest = array[i];
    }
  }
  return biggest - smallest;
}

function contains(array, number) {
  for (let i = 0; i < array.length; i++) {
    if (array[i] === number) {
      return true;
    }
  }
  return false;
}

function average(array) {
  let total = 0;
  if (array.length === 0) {
    return total;
  }
  for (let i = 0; i < array.length; i++) {
    total += array[i];
  }
  return total / array.length;
}

function countdown() {
  for (let i = 5; i > 0; i--) {
    console.log(i);
  }
}
countdown();

