files.content:
body {
  background-color: #3E416D;
  color: whitesmoke;
  font-family: 'Lato', sans-serif;
}

a {
  text-decoration: none;
  color: inherit;
}

h1,
nav,
main,
.offer {
  text-align: center;
}

img {
  height: auto;
  width: 300px;
}

h3 {
  text-align: center;
}

button {
  border: none;
  border-radius: 5px;
}

.post-container {
  display: flex;
  align-items: center;
  flex-direction: column;
}

.post {
  background-color: #1E96FF;
  color: black;
  width: 80%;
  padding: 5px;
  border-radius: 7px;
  margin-top: 25px;
}

.comment {
  background-color: whitesmoke;
  color: black;
  width: 75%;
  padding: 5px;
  border-radius: 7px;
  margin-top: 25px;
}

.footer {
  text-align: center;
  position: relative;
  left: 0;
  bottom: 0;
  width: 100%;
  padding-bottom: 10px;
}

