const spoonsForCoffee = () => new Promise(function(resolve) {
  let enoughSugar = false;
  for (let spoons = 0; spoons <= 2; spoons++) {
    if (spoons === 2) {
      enoughSugar = true;
    }
  }
  resolve(enoughSugar);
  console.log('This coffee has two spoons of sugar');
});
const isEnoughSugarForMyCoffee = sugar => new Promise(function(resolve) {
  if (sugar) {
    resolve("yummy! It tastes good!");
  } else {
    reject('Another spoon of sugar, please.');
  }
});
const putSugarOnMyCoffee = () => {
  const isEnoughSugar = spoonsForCoffee();
  const response = isEnoughSugarForMyCoffee(isEnoughSugar);
  console.log(response);
};
putSugarOnMyCoffee();

