files.content:
body {
  background: linear-gradient(90deg, #ABC6CC, #DBE6F6);
  font-family: 'Gill Sans', sans-serif;
  font-weight: 100;
}

.song {
  border: 1px solid gray;
  padding: 10px;
  border-radius: 5px;
  height: 20px;
  background-color: #DFE6E9;
  color: black;
  font-size: 10px;
}

#album {
  height: 400px;
  margin-top: 50px;
  margin-bottom: 50px;
  align-items: center;
}

#album img {
  height: 100px;
  width: 100px;
  border-radius: 10px;
  border: solid 5px white;
}

#songlist {
  height: 150px;
}

#description {
  margin-left: 20px;
  margin-right: 20px;
}

#album-metadata {
  margin-left: 10px;
  margin-right: 10px;
}

