body,
html {
  height: 100%;
  margin: 0;
  font-family: sans-serif;
}

.chat-header {
  padding: 10px;
  background-color: salmon;
}

.chat-content {
  margin: auto;
  overflow-y: auto;
  padding: 12px 20px;
  display: flex;
  flex-direction: column;
  width: 80%;
}

.message {
  display: flex;
  align-items: flex-end;
  margin: 12px 0;
}

.bot-message .message-content {
  max-width: 70%;
  padding: 12px 16px;
  background-color: whitesmoke;
  border-radius: 16px;
}

.message .message-avatar {
  font-size: 16px;
  margin-right: 12px;
  color: whitesmoke;
  background-color: salmon;
  padding: 10px 8px;
  border-radius: 50%;
}

const joke = [
    "What did 0 say to 8?",
    "Nice belt."
];

const chatContent = document.querySelector(".chat-content");

function appendBotMessage(messageText) {
    const messageDiv = document.createElement("div");
    messageDiv.className = "message bot-message";

    const avatar = document.createElement("div");
    avatar.className = "fas fa-robot message-avatar";

    const contentDiv = document.createElement("div");
    contentDiv.className = "message-content";
    contentDiv.textContent = messageText;

    messageDiv.append(avatar, contentDiv);
    chatContent.appendChild(messageDiv);

    chatContent.scrollTop = chatContent.scrollHeight;
}

appendBotMessage("Hello! I am Comedian Bot, here to make you laugh. Let me think of a joke.");

setTimeout(function() {
    appendBotMessage("Ok, got one.");
}, 1000);

setTimeout(function() {
    appendBotMessage(joke[0]);
}, 1500);

setTimeout(function() {
    appendBotMessage(joke[1]);
}, 2000);

