const express = require("express");
const app = express();
const allFoods = [
  {
    category: "Breakfast",
    name: "Banana",
    calories: 110,
  },
  {
    category: "Lunch",
    name: "Spinach Bruschetta",
    calories: 110,
  },
  {
    category: "Dinner",
    name: "Smoked Salmon",
    calories: 110,
  },
];
app.get("", (request, response) => {
  const foundFood = allFoods.find(
    (eachFood) => eachFood.name === request.params.searchedFood,
  );
  response.status(200).send(foundFood);
});
app.listen(3000, () => {
  console.log("listening on port 3000");
});

files.solvedContent:
const express = require("express");
const app = express();
const allFoods = [
  {
    category: "Breakfast",
    name: "Banana",
    calories: 110,
  },
  {
    category: "Lunch",
    name: "Spinach Bruschetta",
    calories: 110,
  },
  {
    category: "Dinner",
    name: "Smoked Salmon",
    calories: 110,
  },
];
app.get("/foods/today/:searchedFood", (request, response) => {
  const foundFood = allFoods.find(
    (eachFood) => eachFood.name === request.params.searchedFood,
  );
  response.status(200).send(foundFood);
});
app.listen(3000, () => {
  console.log("listening on port 3000");
});
