import React from "react";
import { createRoot } from "react-dom/client";
import App from "./App"

const rootElement = document.getElementById("root");
const root = createRoot(rootElement);
root.render(<App />);

import React from "react";
import ScoreKeeper from "./ScoreKeeper";

const App = () => {
  return <ScoreKeeper />;
};

export default App;

import React from "react";

const ScoreView = (props) => {
  return (
    <div>
      <h2>
        {props.teamName}: {props.score}
      </h2>
    </div>
  );
};

export default ScoreView;

import React, { useState } from "react";
import ScoreView from "./ScoreView";

const ScoreKeeper = () => {
  const [teamOneScore, setTeamOneScore] = useState(0);
  const [teamTwoScore, setTeamTwoScore] = useState(0);

  return (
    <div>
      <h1>Score Keeper</h1>
      <ScoreView teamName="Team One" score={teamOneScore} />
      <button onClick={() => setTeamOneScore(teamOneScore + 1)}>
        +1 Team One
      </button>

      <ScoreView teamName="Team Two" score={teamTwoScore} />
      <button onClick={() => setTeamTwoScore(teamTwoScore + 1)}>
        +1 Team Two
      </button>
    </div>
  );
};

export default ScoreKeeper;

