      {!isGameOver && (
        <>
          <input
            onChange={handleInputChange}
            maxLength={targetWord.length}
            placeholder="Enter your guess"
          />
          <button onClick={handleGuess}>Guess</button>
        </>
      )}
    </div>
  );
};

